        INSERT INTO tb_loopImg(

			LOOP_URL,
			LOOP_TYPE,
			LOOP_NAME
        ) VALUES (

		    #{loopUrl},
		    #{loopType},
		    #{loopName}
        )

mapper.update:
UPDATE tb_loopImg
LOOP_URL = #{loopUrl}, LOOP_TYPE = #{loopType}, LOOP_NAME = #{loopName},
WHERE id = #{id}
