mapper.select:
SELECT

FROM tb_material a
WHERE a.id = #{id} and del_flag = ${@cn.org.upthink.persistence.mybatis.entity.BaseEntity@DEL_FLAG_NORMAL}

mapper.update:
UPDATE tb_material
SALE_PRICE = #{salePrice}, DOWNLOAD_URL = #{downloadUrl}, MATERIAL_TITLE = #{materialTitle}, IMG_URL = #{imgUrl}, SIZE = #{size}, BASE_PRICE = #{basePrice},
WHERE id = #{id}
