mapper.select:
SELECT

FROM tb_user a
WHERE a.id = #{id} and del_flag = ${@cn.org.upthink.persistence.mybatis.entity.BaseEntity@DEL_FLAG_NORMAL}

SELECT

FROM tb_user a
where a.open_id = #{openId}

mapper.update:
UPDATE tb_user
HEAD_IMG = #{headImg}, OPEN_ID = #{openId}, NICK_NAME = #{nickName}, USER_NAME = #{userName},
WHERE id = #{id}

mapper.insert:
        INSERT INTO tb_user_role(
			user_id,
			role_id
        ) VALUES (
		    #{id},
			#{role.id}
        )
